    var username = req.body.username
    var role = req.body.role
    infoModel.create({
        _id : id,
        username: username,
        role: role,
        school: school,
        Class: Class,
        score: 0
    })
    .then(data=>{
        res.json("Success")
    })
    .catch(err=>{
        res.status(500).json("Error server")
    })

})

router.put('/:token', (req, res, next)=>{
    var id = jwt.verify(token, 'it_nht')
